import { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import "../App.css";
import { Header } from "./Header";
import { useSelector, useDispatch } from "react-redux";
import { signupActions } from "../store";
import "bootstrap/dist/css/bootstrap.min.css";
import "./login.css";

function Login() {
  const [backendData, setBackendData] = useState([{}]);
  useEffect(() => {
    fetch("http://localhost:5000/api2")
      .then((response) => response.json())
      .then((user) => {
        console.log('login data',user.data)
        setBackendData(user.data);
      });
  }, []);


  
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  

  const navigate = useNavigate();
  const backendUsers = backendData.find(
    (user) => user.username === username && user.password === password
  );

  const users = useSelector((state) => state.signup.users);
  // const payload = users.find(
  //   (user) => user.username === username && user.password === password
  // );
  const dispatch = useDispatch();
  function login(e) {
    e.preventDefault();
    fetch("http://localhost:5000/api3", {
      method: 'POST',
      headers: {"Content-Type": "application/json"},
      body:JSON.stringify({username, password })
    })

    console.log('backend users are',backendUsers);
    // console.log(payload);
    if (backendUsers) {
      // fetch("http://localhost:5000/api3", {
      //   method: 'POST',
      //   headers: {"Content-Type": "application/json"},
      //   body:JSON.stringify({username, password })
      // })
      // console.log("backend users are", backendUsers);
      // console.log("payload is", payload);
      // dispatch(signupActions.info(payload));
      // console.log(dispatch(signupActions.login(username)))
      // dispatch(signupActions.login(username));

      navigate("/welcome");
    } else {
      alert("incorrect username or password");
    }
  }


  //   if (payload) {
  //     // console.log("backend users are", backendUsers);
  //     // console.log("payload is", payload);
  //     dispatch(signupActions.info(payload));
  //     // console.log(dispatch(signupActions.login(username)))
  //     dispatch(signupActions.login(username));

  //     navigate("/welcome");
  //   } else {
  //     alert("incorrect username or password");
  //   }
  // }

  return (
    <>
      <nav className="navbar navbar-dark bg-dark">
        <Header
          element2={
            <Link to="signup">
              <button className="btn btn-primary btn-sm nav-button">
                Sign up
              </button>
            </Link>
          }
        />
      </nav>

      <form onSubmit={login} className="rounded p-4 p-sm-5">
        <h1 className="heading"> Login </h1>

        <div className="form-group">
          <label className="mb-2">Username</label>
          <input
            className="form-control mb-2"
            name="username"
            type="text"
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>

        <div className="form-group">
          <label className="mb-2">Password</label>
          <input
            className="form-control mb-2"
            name="password"
            type="password"
            label="Password"
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <div className="button-div">
          <button type="submit" className="btn btn-primary mt-3 login-button">
            Login
          </button>
          <p className="login-footer">
            not a user?{" "}
            <Link to="signup" className="login-footer">
              click here
            </Link>{" "}
            to register
          </p>
        </div>
      </form>
    </>
  );
}

export default Login;
